mapper.select:
SELECT m.*,(SELECT u.username FROM _users u WHERE m.manager=u.id AND u.type=1) AS managerName 
FROM _results m WHERE 1=1
	AND id = #{id}
	AND m.course LIKE '%${course}%'
	AND m.score >= #{score}
	 AND m.score <= #{score} 
	AND registrationId = #{registrationId}
	AND m.manager = #{manager}
	AND m.createTime >= #{createTime}
	 AND m.createTime <= #{createTime}
